<?php /**
* Customizer Defaults
*
* The following filter is used to setup the customizer defaults which are setup by your child theme
*
*/
function crafthouse_customizer_defaults( $defaults ){

		$defaults = array(
			'header-menu-layout' => 'header-logo-center',
			'header-background-color' => FALSE,
			'footer-background-color' => '#000000',
			'header-width' => 'layout-boxed',
			'body-fonts' => 'Roboto Condensed',
			'footer-sidebar-count' => '3',
			'archive-left-sidebar' => '0',
			'archive-right-sidebar' => '1',

			// WooCommerce / StoreKit Defaults
			'woocommerce-show-menu-cart-mini-cart' => '1',
			'woocommerce-product-list-thumbnails-flip' => '1',
			'woocommerce-product-list-products-per-row' => '2',
			'single-left-woocommerce-sidebar' => FALSE,
			'single-right-woocommerce-sidebar' => FALSE,
			'archive-left-woocommerce-sidebar' => '1',
			'archive-right-woocommerce-sidebar' => '0',
		);

		return $defaults;
}
