function crafthouse_product_image_catalog_sizes( $size ){
	$size['width'] = '640';
	$size['height'] = '440';

	return $size;
}
add_filter( 'woocommerce_get_image_size_shop_catalog', 'crafthouse_product_image_catalog_sizes' );

function crafthouse_product_image_single_sizes( $size ){
	$size['width'] = '800';
	$size['height'] = '560';

	return $size;
}
add_filter( 'woocommerce_get_image_size_shop_single', 'crafthouse_product_image_single_sizes' );

function crafthouse_product_image_thumbnail_sizes( $size ){
	$size['width'] = '220';
	$size['height'] = '220';

	return $size;
}
add_filter( 'woocommerce_get_image_size_shop_thumbnail', 'crafthouse_product_image_thumbnail_sizes' );



function crafthouse_set_content_width() {
	global $content_width;

	$left_sidebar_active = layers_can_show_sidebar( 'left-sidebar' );
	$right_sidebar_active = layers_can_show_sidebar( 'right-sidebar' );

	if( is_page_template( LAYERS_BUILDER_TEMPLATE ) ) {
		$content_width = 1280;
	} else if( is_page_template( 'template-both-sidebar.php' ) ||
		is_page_template( 'template-left-sidebar.php' ) ||
		is_page_template( 'template-right-sidebar.php' ) ){
		$content_width = 800;
	} elseif ( is_page_template( 'template-blog.php' ) ) {
		$content_width = 1280;
	} elseif( $left_sidebar_active || $right_sidebar_active ){
		$content_width = 800;
	}
}
add_action( 'template_redirect', 'crafthouse_set_content_width', 50 );
